AREA UserModules (ROM, REL, CON)

;;=============================================================================
;;  FUNCTION NAME: _DelSig_ADConversion_ISR:
;;=============================================================================
;;
_DelSig_ADConversion_ISR:
   push  A
.ClrTst:
   M8C_ClearIntFlag DelSig_INT_CLR_REG, DelSig_INT_MASK
   mov   A, reg[DEC_DH]
   cmp   A, >(UMAX+1)                            ; Is value >= 2^RES?
   jc    .NoOverflow                             ;    No, Data is in normal range
   mov   A, reg[DEC_DL]
   mov   [DelSig_iResult+MSB], >UMAX             ;   Yes, limit to 2^RES - 1
   mov   [DelSig_iResult+LSB], <UMAX             ;          as expected
   jmp   .ConversionReady

.NoOverflow:
   mov   [DelSig_iResult+MSB], A
   mov   A, reg[DEC_DL]
   mov   [DelSig_iResult+LSB], A

.ConversionReady:
   tst   reg[DelSig_INT_CLR_REG], DelSig_INT_MASK
   jnz   .ClrTst
IF DelSig_2S_COMPLEMENT
   ; Internal hardware format is unsigned; optionally convert so zero is half scale
   sub   [DelSig_iResult+MSB], >(UMAX+1)>>1
ENDIF
   mov [DelSig_bfStatus], DelSig_DATA_READY_BIT                

   ;@PSoC_UserCode_BODY@ (Do not change this line.)
   ;---------------------------------------------------
   ; Insert your custom code below this banner
   ;---------------------------------------------------


   ;---------------------------------------------------
   ; Insert your custom code above this banner
   ;---------------------------------------------------
   ;@PSoC_UserCode_END@ (Do not change this line.)

   pop A
   reti

